mapper.delete:

delete from tr_product_order
where ORDER_ID = #{orderId,jdbcType=INTEGER}
  and PRODUCT_ID = #{productId,jdbcType=INTEGER}

mapper.insert:

insert into tr_product_order (ORDER_ID, PRODUCT_ID, count
  )
values (#{orderId,jdbcType=INTEGER}, #{productId,jdbcType=INTEGER}, #{count,jdbcType=SMALLINT}
  )

mapper.update:

update tr_product_order
set count = #{count,jdbcType=SMALLINT}
where ORDER_ID = #{orderId,jdbcType=INTEGER}
  and PRODUCT_ID = #{productId,jdbcType=INTEGER}

mapper.select:

select ORDER_ID, PRODUCT_ID, count
from tr_product_order
where ORDER_ID = #{orderId,jdbcType=INTEGER}
  and PRODUCT_ID = #{productId,jdbcType=INTEGER}
